import React, { useState} from 'react'

function Upload() {
  const [fileState, setFileState] = useState("")
  const [imagePreview, setImagePreview] = useState('')
  const handleFileChange = (e) => {
    e.preventDefault()
    const file = e.target.files[0]
    console.log(e.tag)
    previewImage(file)
  }

  const previewImage = (image) => {
    const reader = new FileReader
    reader.readAsDataURL(image)
    reader.onloadend = () => {
        setImagePreview(reader.result)
    }
  }
  return (
    <div className="">
      <form  className='row mt-5'>
        <div className="form-gruop d-flex flex-column col-5 m-auto">
          <input type="file" className="form-control mb-3" name="image" value={ fileState } onChange={ handleFileChange} />
          <button className="btn btn-primary h2 w-50 m-auto ">
            Submit
          </button>
        </div>
      </form>
      {imagePreview && 
        <div className='mt-5'>
          <h2 className='text-primary h2'>Selected Images</h2>
          <img src={ imagePreview} alt="img"  className='w-50 h-50 image'/>
        </div>}
   </div>
  )
}

export default Upload
